mapper.select:
with recursive CommentTree as ( #루트 댓글 select c.bbs_no, c.id, c.c_no, c.parent_comment, 1 as level, c.comment, c.postdate, c.deleted, p.pro_filepath as pro_filepath from commentline c join profile p on c.id = p.id where c.parent_comment is null
union all
#자식댓글 select c.bbs_no, c.id, c.c_no, c.parent_comment, ct.level + 1 as level, c.comment, c.postdate, c.deleted, p.pro_filepath as pro_filepath from commentline c join profile p on c.id = p.id inner join CommentTree ct on c.parent_comment = ct.c_no ) select * from CommentTree order by level, c_no;
